* {
  box-sizing: border-box; }

body {
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: flex-end;
  align-items: flex-end;
  overflow: hidden;
  margin: 0;
  padding: 0; }

@keyframes appear {
  from {
    opacity: 0; } }

@keyframes rotate {
  from {
    transform: rotateZ(-180deg); } }

@keyframes scale {
  from {
    transform: scale(0); } }

@keyframes dial {
  from {
    transform: scale(1); }
  to {
    transform: scale(0.5); } }

.background {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-repeat: no-repeat;
  background-size: cover;
  background-position: 50%;
  opacity: 1;
  animation: appear .5s .1s backwards; }

.digital,
.analog {
  margin: 1vmin;
  z-index: 1; }

.analog {
  transform: scale(1);
  animation: scale 0.5s 0.1s backwards cubic-bezier(0.25, 0.46, 0.18, 1.3); }
  .analog .dial,
  .analog .face,
  .analog .marker {
    position: absolute;
    width: 100%;
    height: 100%;
    transform-origin: center; }
  .analog .dial,
  .analog .face {
    z-index: 1; }
  .analog .dial {
    border-radius: 50%;
    border-width: 9px;
    border-style: solid;
    animation: dial 0.5s 0.1s both cubic-bezier(0.25, 0.46, 0.18, 1.3); }
  .analog .face {
    opacity: 1;
    animation: appear .5s .6s backwards; }
  .analog .marker {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 1.5%;
    height: 1.5%;
    margin: -.65%;
    border-radius: 50%;
    background-color: currentColor; }
  .analog .minutes-container,
  .analog .minute-hand-container,
  .analog .hours-container {
    width: 100%;
    height: 100%;
    animation: rotate 1s cubic-bezier(0.75, 0.15, 0.1, 1); }
  .analog .hours,
  .analog .minutes {
    width: 100%;
    height: 100%;
    -webkit-mask-image: url("ac72afbfe5dfff59d4947cc9261ad07c.png");
    -webkit-mask-size: 115%;
    -webkit-mask-position: 50%;
    border-radius: 50%;
    overflow: hidden; }
  .analog .hours-container,
  .analog .minute-hand-container {
    margin-top: -100%; }
  .analog .minute-hand {
    width: 5px;
    height: 50%;
    margin-left: calc(50% - 2px);
    transform-origin: bottom;
    box-shadow: -1px 0 15px 0 rgba(0, 0, 0, 0.2); }

.digital {
  font-family: -apple-system, system-ui;
  font-weight: 300;
  font-size: 8vmin;
  -webkit-font-smoothing: subpixel-antialiased;
  color: white;
  text-shadow: 1px 1px 2px #161515;
  animation: appear .5s .6s backwards; }

.date,
.time {
  display: block;
  line-height: 1; }

.date {
  font-size: .3em;
  letter-spacing: .06em;
  text-align: right; }

.time {
  font-size: 1em;
  text-align: right; }
